  levels: number[] = [
    0, 10, 30, 90, 270, 510, 830, 1090, 1570, 2000,
    4000, 7000, 9999, 15000, 20000, 50000
  ];
  maximo: boolean = false;
  level: number = 0;
  exp: number = 0;
  xpGanha: number = 0;
  expTotal: number = 0;
  levelUp: number = 0;

  calcularXP() {
    if (this.maximo) {
      alert('Seu Level jÃ¡ estÃ¡ no mÃ¡ximo.');
      return;
    }

    let xp = this.xpGanha;
    this.expTotal += xp;

    this.levelUp = 0;

    while (xp > 0) {
      if ((this.exp + xp) >= this.levels[this.level]) {
        this.level++;
        this.levelUp++;
        xp -= (this.levels[this.level] - this.exp);
        this.exp = 0;

        if (this.levels[this.level] === undefined) {
          alert('ParabÃ©ns! VocÃª atingiu o nÃ­vel mÃ¡ximo.');
          this.maximo = true;
          xp = 0;
        }
      } else {
        this.exp += xp;
        xp = 0;
      }
    }
  }
